	gr.initialize();
	gr.u_requested_for = '6816f79cc0a8016401c5a33be04be441'; //admin
	gr.u_requested_for_email = 'admin@example.com';
	gr.u_request_type = 'legal';
	gr.u_what_needed = 'legal1';
	var tempWhen =  new GlideDateTime();
	tempWhen.addDaysLocalTime(i);
	gr.u_when_needed =tempWhen.toString();
	gr.short_description = 'Random NeedIt ' + (i+1);
	gr.description = 'NeedIt created by script ';
	gr.priority = '4';//low
	gr.state = '13'; //requested
	gr.insert();

	i++;
}
